$("input").each(function(){var a=$(this)[0].type;if(!$(this)[0].placeholder)switch(a){case"text":$(this)[0].placeholder="Enter the Text";break;case"email":$(this)[0].placeholder="Enter the Email Address";break;case"number":$(this)[0].placeholder="Enter the Number";break;case"password":$(this)[0].placeholder="Enter the Password";break;case"tel":$(this)[0].placeholder="Enter the Mobile No.";break;case"url":$(this)[0].placeholder="Enter the URL";break;default:$(this)[0].placeholder="Enter the Data"}}),$("textarea").each(function(){$(this)[0].placeholder||$(this).attr("placeholder","Enter the Message")});